from django.shortcuts import render
from bs4 import BeautifulSoup
import requests
# from requests.compat import
from urllib.parse import quote_plus
from django.http import HttpResponse
from polls.models import Search

# Create your views here.
BASE_CRAIGSLIST_URL = 'https://losangeles.craigslist.org/search/bbb?query=%s'
IMAGE_CRAIGSLIST_URL = 'https://images.craigslist.org/%s_300x300.jpg'


def home(request):
    return render(request, 'base.html')


def new_search(request):
    search = request.POST.get('search')
    string_search = str(search)
    final_url = BASE_CRAIGSLIST_URL % (quote_plus(string_search))
    Search.objects.create(searches=search)
    response = requests.get(final_url)
    soup = BeautifulSoup(response.text, features='html.parser')
    post_listings = soup.find_all('li', {'class': 'result-row'})
    final_listings_list = []
    for post in post_listings:

        post_title = post.find(class_='result-title hdrlnk').text
        post_url = post.find('a').get('href')
        if post.find(class_='result-price'):
            post_price = post.find(class_='result-price')
        else:
            post_price = "N/A"
        if post.find(class_='result-image').get('data-ids'):
            post_image = post.find('a').get('data-ids').split(',')[0]
            final_post_img = post_image.strip('1:')
            final_img_url = IMAGE_CRAIGSLIST_URL % (final_post_img)
        else:
            final_img_url = 'data:image/jpeg;base64,'

        final_listings = [(post_title, post_url, post_price, final_img_url), ]
        final_listings_list.append(final_listings[0])

    front_end_stuff = {
        'search': search,
        'final_listings_list': final_listings_list,

    }
    return render(request, 'polls/new_search.html', front_end_stuff)


def about(request):
    return render(request, 'polls/about.html')


def contact_us(request):
    return render(request, 'polls/contact_us.html')
